mapper.graphql:

	query(
		$owner: String!, 
		$repo: String!, 
		$type: [String!], 
		$cursor: String, 
		$size: Int!
	) {
	  meta {
	    hookIpAddresses
	  }
	  viewer {
	    bio
	    name
	    login
	    email
	    company
	    location
	  }
	  repository(owner: $owner, name: $repo) {
	    issues(
		    first: $size, 
		    labels: $type, 
		    states: CLOSED, 
		    #PAGE_DIRECTION#: $cursor,
		    filterBy: {
		    	assignee: $owner
		    }, orderBy: {
		    	field: CREATED_AT, direction: DESC
		    }
	    ) {
	      nodes {
	        id,
	        number,
	        title,
	        body,
	        closedAt,
	        labels(first: 10) {
	          nodes {
	            name,
	            color,
	            description
	          }
	        },
	        reactionGroups {
	          content,
	          createdAt,
	          users(first: 5) {
	            totalCount
	            nodes {
	              name
	            }
	          }
	        },
	        author {
	          login
	        },
	      	comments(first: 10) {
	      	  nodes {
	      	    author {
	      	      login
	      	    },
	      	    id,
	      	    bodyText,
	      	    publishedAt,
	      	    reactionGroups {
	      	      content,
	      	      createdAt,
	      	      users(first: 5) {
	      	        totalCount,
	      	        nodes {
	      	          name
	      	        }
	      	      }
	      	    }
	      	  }
	      	}
	      },
	      totalCount,
	      pageInfo {
	        hasPreviousPage,
	        hasNextPage,
	        startCursor,
	        endCursor
	      }
	    }
	  }
	}


	query ($id: ID!) {
	  meta {
	    hookIpAddresses
	  }
	  node(id: $id) {
	    ... on Issue {
	      id,
	      number,
	      title,
	      body,
	      closedAt,
	      labels(first: 10) {
	        nodes {
	          name,
	          color,
	          description
	        }
	      },
	      reactionGroups {
	        content,
	        createdAt,
	        users(first: 5) {
	          totalCount
	          nodes {
	            name
	          }
	        }
	      },
	      author {
	        login
	      },
	      comments(first: 10) {
	        nodes {
	          author {
	            login
	          },
	          id,
	          bodyText,
	          publishedAt,
	          reactionGroups {
	            content,
	            createdAt,
	            users(first: 5) {
	              totalCount,
	              nodes {
	                name
	              }
	            }
	          }
	        }
	      }
	    }
	  }
	}
