graphql.query-blog-issues:

    query($ghp_username:String!, $ghp_repository:String!, $ghp_labels:[String!]!) {
        repository(owner: $ghp_username,name: $ghp_repository) {
            issues(labels: $ghp_labels, states: CLOSED, first: 10) {
                nodes {
                    id,
                    number,
                    url,
                    title,
                    body
                }
            }
        }
    }

graphql.query-topic-issues:

    query($ghp_username:String!, $ghp_repository:String!, $ghp_labels:[String!]!) {
        repository(owner: $ghp_username,name: $ghp_repository) {
            issues(labels: $ghp_labels, first: 10) {
                nodes {
                    id,
                    number,
                    url,
                    title,
                    createdViaEmail,
                    publishedAt,
                    comments(first: 100) {
                        nodes {
                            id,
                            databaseId,
                            url,
                            bodyText,
                            publishedAt,
                        }
                    }
                }
            }
        }
    }
